// Import the package
const keepServerAlive = require('keep-server-alive');

// Use it just like ping-me
const { stopPinging } = keepServerAlive.pingMe({
  url: 'https://your-app.example.com',
  interval: 60000 // 1 minute
});

// Stop pinging when needed
process.on('SIGINT', () => {
  stopPinging();
  process.exit(0);
});
